// src/store/index.js
// src/store/index.js
import { createStore } from 'vuex';
import gamesData from './gamesData.json'; // Asegúrate de que la ruta sea correcta

// No necesitas usar `formattedJuegos` si `gamesData` ya está en el formato correcto
// Si necesitas formatear los juegos, puedes hacerlo directamente aquí:
const formattedJuegos = gamesData.map(juego => ({
  ...juego,
  stock: Number(juego.stock) // Convertir el stock a número si es necesario
}));

export default createStore({
  state: {
    juegos: formattedJuegos, // Usar la lista formateada de juegos
  },
  mutations: {
    modificarStock(state, { codigo, cantidad }) {
      const juego = state.juegos.find((juego) => juego.codigo === codigo);
      if (juego && juego.stock + cantidad >= 0) {
        juego.stock += cantidad; // Modifica el stock si la cantidad es válida
      }
    },
  },
  actions: {
    incrementarStock({ commit }, codigo) {
      commit('modificarStock', { codigo, cantidad: 1 }); // Incrementa el stock
    },
    disminuirStock({ commit }, codigo) {
      commit('modificarStock', { codigo, cantidad: -1 }); // Disminuye el stock
    },
  },
});
