// src/store/index.js
import { createStore } from "vuex";
import gamesData from "./gamesData.json"; // Asegúrate de que la ruta sea correcta

export default createStore({
  state: {
    games: gamesData.map((game) => ({
      ...game,
      stock: parseInt(game.stock, 10), // Convierte el stock a número
      precio: parseInt(game.precio, 10), // Convierte el precio a número
    })),
  },
  mutations: {
    updateStock(state, { codigo, cantidad }) {
      const game = state.games.find((game) => game.codigo === codigo);
      if (game) {
        game.stock += cantidad;
      }
    },
  },
  actions: {
    modifyStock({ commit }, payload) {
      commit("updateStock", payload);
    },
    accionIncrementar({ commit }) {
      setTimeout(() => {
        commit("incrementar", 10);
      }, 3000);
    },
    accionDecrementar({ commit }) {
      setTimeout(() => {
        commit("incrementar", -10);
      }, 3000);
    },
  },

  getters: {
    allGames: (state) => state.games,
  },
});
