// src/store/index.js
import { createStore } from "vuex";
import gamesData from "./gamesData.json"; // Asegúrate de que la ruta sea correcta

// src/store/index.js
export default createStore({
  state: {
    games: gamesData,
  },
  mutations: {
    updateStock(state, { codigo, cantidad }) {
      const game = state.games.find(game => game.codigo === codigo);
      if (game) {
        game.stock += cantidad;
      }
    },
    incrementarStock(state, codigo) {
      const game = state.games.find(game => game.codigo === codigo);
      if (game) {
        game.stock++;
      }
    },
    decrementarStock(state, codigo) {
      const game = state.games.find(game => game.codigo === codigo);
      if (game && game.stock > 0) {
        game.stock--;
      }
    },
  },
  actions: {
    modifyStock({ commit }, payload) {
      commit('updateStock', payload);
    },
    accionIncrementar({ commit }, codigo) {
      commit('incrementarStock', codigo);
    },
    accionDecrementar({ commit }, codigo) {
      commit('decrementarStock', codigo);
    },
  },
});
